import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import MyModal from "./modal";
import { AiFillEdit, AiFillDelete } from "react-icons/ai";
import Button from "react-bootstrap/Button";
import ButtonGroup from "react-bootstrap/ButtonGroup";

function JsonDataDisplay() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(50);
  const [selectedTeam, setSelectedTeam] = useState(50);

  const fetchDate = (async) => {
    fetch("teams.json")
      .then((res) => res.json())
      .then((teams) => {
        console.log(teams);
        setData(teams);
        setTotalRows(teams.length);
        setLoading(false);
      });
  };

  const columns = [
    {
      name: "League",
      selector: (row) => row.LeagueID,
      //   sortable: true,
      wrap: true,
      grow: 1,
    },
    {
      name: "Name",
      selector: (row) => row.Name,
      wrap: true,
      // center: true,
      grow: 3,
    },
    {
      name: "Oddset Name",
      selector: (row) => row.OddsetName,
      wrap: true,
      // center: true,
      grow: 3,
    },

    {
      name: "TV4 Name",
      selector: (row) => row.TV4Name,
      wrap: true,
      // center: true,
      grow: 3,
    },
    {
      name: "Actions",
      button: true,
      cell: (row) => (
        <ButtonGroup aria-label="Basic example">
          <Button onClick={(e) => handleButtonClick(e, row)}>
            <AiFillEdit />
          </Button>
          <Button onClick={(e) => handleButtonClick(e, row)}>
            <AiFillDelete />
          </Button>
        </ButtonGroup>
      ),
    },
  ];

  const handleButtonClick = (e, team) => {
    e.preventDefault();
    var index = data.indexOf(team);
  };

  const paginationComponentOptions = {
    rowsPerPageText: "page no",
    rangeSeparatorText: "-",
    selectAllRowsItem: true,
    selectAllRowsItemText: "All",
  };

  useEffect(() => {
    fetchDate();
  }, []);

  const onAddedTeam = (team) => {
    data.unshift(team);
    setData(data);
  };

  const onEditedTeam = (team, idx) => {
    data[idx] = team;
    setData(data);
  };

  const onDeletedTeam = (idx) => {
    data.splice(idx, 1);
    setData(data);
  };

  const saveData = () => {};

  return (
    <div>
      <MyModal

        onAdd={onAddedTeam}
        onDelete={onDeletedTeam}
        onEdit={onEditedTeam}
      />

      <DataTable
        title="FBK TEAMS"
        progressPending={loading}
        columns={columns}
        data={data}
        paginationPerPage={perPage}
        dense
        pagination
        paginationComponentOptions={paginationComponentOptions}
        paginationTotalRows={totalRows}
        fixedHeader
      />
    </div>
  );
}

export default JsonDataDisplay;
