import React, { useEffect, useState } from "react";
import teams from '../teams.json'
import DataTable from 'react-data-table-component';

 function JsonDataDisplay(){

    const [data, setData] = useState([]);
	const [loading, setLoading] = useState(false);
	const [totalRows, setTotalRows] = useState(0);
	const [perPage, setPerPage] = useState(20);

    const fetchDate = async  => {
		setLoading(true);

        fetch('../teams.json')
        .then(data => data.json())
        .then(teams => {
            var d = [];
            setData(teams)
            setTotalRows(teams.length)
            setLoading(false);
        })
	};


    const columns = [
        {
            name: 'League',
            selector: row => row.LeagueID,
            sortable:  true,
            wrap: true,
            grow: 1, 
        },
        {
            name: 'Name',
            selector: row => row.Name,
            wrap: true,
            // center: true,
            grow: 3, 
        },
        {
            name: 'Oddset Name',
            selector: row => row.OddsetName,
            wrap: true,
            // center: true,
            grow: 3, 
        },

        {
            name: 'TV4 Name',
            selector: row => row.TV4Name,
            wrap: true,
            // center: true,
            grow: 3, 
        },

    ];

    const paginationComponentOptions = {
        rowsPerPageText: 'page no',
        rangeSeparatorText: '-',
        selectAllRowsItem: true,

        selectAllRowsItemText: 'All',
    };

    useEffect(() => {
        fetchDate();

	}, []);


    return(
        <div>
            <DataTable 
            columns={columns}
            data={teams}
            dense
			pagination
			paginationServer
            paginationComponentOptions={paginationComponentOptions}

            />

        </div>
    )
 }

 export default JsonDataDisplay;
